-- Dumping data for table `books`
--

INSERT INTO `books` (`id`, `category_id`, `sub_category_id`, `publisher_id`, `author_id`, `language_id`, `book_series_id`, `name`, `code`, `regular_price`, `selling_price`, `discount`, `stock`, `status`, `short_description`, `long_description`, `meta_title`, `meta_description`, `tags`, `pages`, `published_date`, `book_format`, `isbn`, `image`, `hit_count`, `sales_count`, `feature_status`, `created_at`, `updated_at`) VALUES
(1, 1, 4, 1, 1, 1, 0, 'দেয়াল', 'HAD001', 450, 338, 25, 10, 1, '‘ভাদ্র মাসের সন্ধা। আকাশে মেঘ আছে। লালচে রঙের মেঘ। যে মেঘে বৃষ্টি হয় না, তবে দেকায় অপূর্ব। এই গাঢ় লাল, এই হালকা হলুদ, আবার চোখের নিমিষে লালের সঙ্গে খয়েরি ম...', '<div class=\"summary-description active\" id=\"js--summary-description\"> <b>ফ্ল্যাপে লিখা কথা</b><br> ‘ভাদ্র মাসের সন্ধা। আকাশে মেঘ আছে। লালচে রঙের মেঘ। যে মেঘে বৃষ্টি হয় না, তবে দেকায় অপূর্ব। এই গাঢ় লাল, এই হালকা হলুদ, আবার চোখের নিমিষে লালের সঙ্গে খয়েরি মিশে সম্পূর্ণ অন্য রঙ। রঙের খেলা যিনি খেলছেন মনে হয় তিনি সিদ্ধান্তহীনতায় ভুগছেন।’<br><br> এভাবেই সূচনা ঘটেছে হুমাযূন আহমেদের চার দশকের বর্ণময় লেখকজীবনের শেষ উপন্যাস ‘দেয়াল’- এর। ২০১১ সালের মাঝামাঝিতে ‘দেয়াল’ রচনা শুরু করেছিলেন তিনি। সে-সময় উপন্যাসের পাঁচটি পর্ব ধারাবাহিকভাবে ‘অন্যদিন’-এর প্রকাশিত হয়। এরপর বেশ কিছুদিন বিরতির পর যুক্তরাষ্ট্রে তাঁর ক্যানসার চিকিৎসা চলাকালে নতুন করে ‘দেয়াল’ রচনায় মনোনিবেশ করেন তিনি, যদিও শেষ পর্যন্ত উপন্যাসটির চূড়ান্ত রূপ দেয়ার সুযোগ পান নি।<br><br> সূচনা-অনুচ্ছেদে আকাশের রঙবদলের খেয়ায় যে সিদ্ধান্তহীনতার কথা বলা হচ্ছে তা বিশেষ ইঙ্গিতবহ। যে সময়কে উপজীব্য করা হয়েছে ‘দেয়াল’-এ, তা একটি সদ্যস্বাধীন জাতির ভাগ্যকাশের চরম অনিশ্চয়তার কাল। উপন্যাসের কিছু চরিত্র বাস্তব থেকে নেওয়া, নাম-ধাম সবই বাস্তব, ঘটনা-পরস্পরাও বাস্তবেরই অংশ। লেখক যেহেতু উপন্যাস লিখেছেন, তাই আছে কিছু কাল্পনিক চরিত্র। গল্প আবর্তিত হয়েছে এদের ঘিরেও।<br><br> নানা ঘটনার ঘনঘটায় ঢাকা পড়ে নি জীবনসৌন্দর্য আর জীবন-সত্যের সন্ধান। ইতিহাসের সত্য আর লেখকের সৃজনী ভাবন্য-দুইয়ে মিলে ‘দেয়াল’ পরিণত হয়েছে একটি হৃদয়গ্রাহী উপাখ্যানে।<br><br> <b>ভূমিকা</b><br> হুমায়ূন আহমেদের অবর্তমানে তার উপন্যাস দেয়াল প্রকাশিত হতে যাচ্ছে। প্রকাশকের ইচ্ছায় আমি তার ভূমিকা লিখছি। বইটির যে কোনো ভূমিকার প্রয়োজন ছিল, আমার তা মনে হয় না।<br><br> গ্রন্থাকারে প্রকাশিত হওয়ার আগেই দেয়াল নিয়ে বিতর্ক দেখা দিয়েছে। বিষয়টা আদালত পর্যন্ত গড়িয়েছে। হাইকোর্টের পরামর্শ-অনুযায়ী লেখক উপন্যাসটির প্রথম প্রকাশিত রূপের পরিবর্তন সাধন করেছেন। গ্রন্থাকারে সেই পরিবর্তিত রূপই প্রকাশ পেতে যাচ্ছে। তারপরও আমার মনে হয়, দেয়াল বিতর্কিত থেকে যাবে।<br><br> বইটিতে দুটি আখ্যান সমান্তরালে চলেছে। প্রথমটি অবন্তি নামে এক চপলমতি ও প্রচলবিরোধী মেয়ের কাহিনি। তার বাবা নিরুদ্দিষ্ট। স্বামীর সঙ্গে সম্পর্ক ত্যাগ করে মা ইসাবেলা স্বদেশ স্পেনে চলে গেছেন। এই দম্পতির কেউ যে সাধারণ বিচারে স্বাভাবিক, তা মনে হয় না। অবন্তি ঢাকা ঢাকায় বাস করে পিতামহ সরফরাজ খানের সঙ্গে- তিনি রক্ষণশীল এবং খেয়ালি- অবন্তিতে লেখা তার মায়ের চিঠি আগে গোপনে খুলে পড়েন, অবন্তির শিক্ষক শফিকের ওপর নজরদারি করেন এবং আরও অনেক কিছু করেন। মুক্তিযুদ্ধের সময় নিরাপত্তার খোঁজে ঢাকা ছেড়ে গ্রামে যান, সেখানেও টিকতে না পেরে আশ্রয় নেন এক পীরের বাড়িতে। এক পাকিস্তানি সেনা-কর্মকর্তা অবন্তিকে দেখে ফেলে বিয়ে করতে চায়। বিপদ থেকে উদ্ধার পেতে পীর নিজের ছেলের সঙ্গে অবন্তির বিয়ে দিয়ে দেন। অবন্তি এ-বিয়ে মেনে নেয় না বটে, কিন্তু হাফেজ জাহাঙ্গীরের সঙ্গে যোগাযোগ ছিন্ন করে না। তা নিয়ে কিছু জটিলতার সৃষ্টি হয়।<br><br> সরফরাজ খানের পুত্রের বন্ধুদের একজন মেজর জেনারেল খালেদ মোশাররফ। এ-বাড়িতে তাঁর আসা-যাওয়া আছে। তাঁর সূত্রে কর্নেল তাহেরও এখানে এসেছেন। এভাবেই প্রথম আখ্যানের সঙ্গে দ্বিতীয় আখ্যানের যোগ সাধিত হয়।<br><br> দ্বিতীয় আখ্যানটি সূচিত হয় মেজর ফারুকের বঙ্গবন্ধু হত্যার পরিকল্পনা নিয়ে। এই পরিকল্পনায় ফারুক ও মেজর রশীদ মেজর জেনারেল জিয়াউর রহমান ও ওসমানীকে জড়িত করে। পরিকল্পনায় ফারুক ও মেজর রশীদ মেজর জেনারেল জিয়াউর রহমান ও খালেদ মোশাররফের অভ্যুত্থান, কারাগারে চার নেতা হত্যা, কর্নেল তাহেরের নেতৃত্বে সিপাহী-জনতার বিপ্লব, জিয়াউর রহমানের মুক্তিলাভ ও ক্ষমতাগ্রহণ, খালেদ মোশাররফ ও কর্নেল হুদার হত্যা এবং তাহেরের ফাঁসিতে উপাখ্যানের সমাপ্তি। তারপরও লেখক দ্রুত ঘটনা বলে গেছেন, উপন্যাসের সমাপ্তি হয়েছে জিয়ার হত্যাকাণ্ডে।<br><br> আমাদের জাতীয় ইতিহাসের এসব শোকাবহ পর্বের বর্ণনায় যে-পরিসর ও ব্যাখ্যা বিশ্লেষণের প্রয়োজন ছিল, বইতে তা দেওয়া হয় নি। বঙ্গবন্ধুর শাসনকালে অনুবস্ত্রের অভাব এবং রক্ষী বাহিনীর অত্যাচার ও তাদের প্রতি সর্বসাধারণের ক্ষোভ ও ঘৃণার কথা উল্লেখ করা হয়েছে। শেখ মুজিবকে বঙ্গপিতা, মহামানব ও বঙ্গবন্ধু বলা হলেও মৃত্যুতে তিনি লেখকের অতটা সহানুভূতি লাভ করেননি যতটা পেয়েছেন তাঁর পরিবারের শিশু ও নারীরা। বঙ্গবন্ধু-হত্যায় মানুষের মধ্যে থেকে যে প্রবল প্রতিবাদ হলো না, বরঞ্চ কোথাও কোথাও আনন্দ-মিছিল হলো, এতে লেখক বিস্মিত (কাদের সিদ্দিকীর প্রতিবাদ সম্পর্কে হুমায়ূনের মন্তব্য : ‘ভারতে তিনি ‘কাদেরিয়া বাহিনী’ তৈরি করে সীমান্তে বাংলাদেশের থানা আক্রমণ করে নিরীহ পুলিশ মারতে লাগলেন। পুলিশ বেচারারা কোনো অর্থেই বঙ্গবন্ধুর হত্যার সঙ্গে জড়িত না, বরং সবার আগে বঙ্গবন্ধুকে রক্ষা করার জন্যে তারা প্রাণ দিয়েছে।”) অবন্তির গৃহশিক্ষক শফিক- যে নিজেকে খুবই ভীতু বলে পরিচয় দেয়, সে-কিন্তু রাস্তায় দাঁড়িয়ে ‘মুজিব হত্যার বিচার চাই’ চলে স্লোগান দেয়, গ্রেপ্তার হয় এবং নিপীড়ন সহ্য করে। খন্দকার মোশতাককে এ-বইতে আমার পাই কমিক চরিত্ররূপে। ‘অসীম সাহসী মুক্তিযোদ্ধা বীর উত্তম খালেদ মোশাররফ’ এবং ‘মহাবীর কর্নেল তাহেরে’র প্রতি লেখকের শ্রদ্ধা প্রকাশ পেয়েছে এবং উপন্যাসে কর্নেল তাহেররকেও দেখি খালেদ মোশাররফের সাহসিকতা ও চরিত্রগুণের প্রশংসা করতে। জিয়াউর রহমানের আর্থিক সততার প্রশংসা আছে, জনগণের শ্রদ্ধা তিনি অর্জন করেছিলেন, তা বলা হয়েছে, সেই সঙ্গে তাঁর ক্ষমতালোভের কথা বলা হয়েছে এবং সরকারি তথ্য উদ্বৃত করে জানানো হয়েছে যে, ১৯৭৭ সালের ৯ অক্টোবর পর্যন্ত তাঁর গঠিত সামরিক আদালতের বিচারে ১১৪৩ জন সৈনিক ও অফিসারকে ফাঁসির দড়িতে ঝুলিতে হয়েছে। হুমায়ূনের মতে, এদের দীর্ঘনিশ্বাস জমা হয় চট্টগ্রামের সার্কিট হাউজে- সেখানে ‘জিয়া প্রাণ হারান তাঁর এক সময়ের সাথী জেনারেল মঞ্জুরের পাঠানো ঘাত বাহিনীর হাতে।’ এই সিদ্ধান্তের সমর্থনে বইতে কোনো তথ্য নেই, বরঞ্চ এই হত্যাকাণ্ডের পশ্চাতে মনজুরের ‘রূপবতী স্ত্রী’র প্রলয়ংকরী স্ত্রীবুদ্ধি কাজ করে থাকতে পারে বলে অনুমান করা হয়েছে। বঙ্কিমচন্দ্র যে বলেছিলেন, ‘উপন্যাস উপন্যাস-উপন্যাস ইতিহাস নহে’, সে কথা যথার্থ। তবে ইতিহাসাশ্রিত উপন্যাসে ইতিহাসের সারসত্য অবিকৃত থাকবে বলে আশা করা হয় এবং কল্পনাপ্রসূত আখ্যানেও কার্যকারণ সম্পর্কের ব্যাখ্যা প্রত্যাশিত।<br><br> দেয়াল উপন্যাসের প্রথমদিকে হুমায়ূন আহমেদ নিজের উল্লেখ করেছে প্রথম পুরুষে, শেষদিকে এসে উত্তমপুরুষে নিজের কথা সে বলে গেছে। আমরা জানতে পারি- অনেকেরই তা অজানা নয় যে- শহীদ পরিবার হিসেবে ঢাকা শহরে হুমায়ূনদের সরকারিভাবে যে বাড়ি বরাদ্দ দেওয়া হয়, রক্ষী বাহিনীর এক কর্মকর্তা তা দখল করে তাদেরকে নির্মমভাবে সেখান থেকে উচ্ছেদ করে পথে নামিয়ে দেন; হুমায়ূনের মা এবং ভাইবোনেরা শুধু চরম অপমানের শিকার হন, তা নয়, নিরাপত্তার সম্পূর্ণ অভাবে পড়েন। বিশ্ববিদ্যালয়ের তরুণ শিক্ষক হুমায়ূনকে বাকশালে যোগ দিতে চাপ দেওয়া হয় এবং চাপের কাছে নতিস্বীকার করে রসায়ন বিভাগে রেজিস্ট্রারের অফিসে এবং উপাচার্যের দপ্তরে ছোটাছুটি করেও শেষ পর্যন্ত সময় উত্তীর্ণ হওয়ার কারণে তার প্রয়াস নিষ্ফল হয়, বাকশালে যোগদান থেকে সে বেঁচেই যায় বলতে হবে। এসব ঘটনা বঙ্গবন্ধুর সরকার সম্পর্কে তার মনে বিরুপ প্রতিক্রিয়ার সৃষ্টি করে। অন্যপক্ষে কর্নেল তাহেরের ভাই আনোয়ার হোসেনের সূত্রে তাহেরের সঙ্গে সে পরিচিত হয়, তাঁর মাকে নিজের মায়ের মতো দেখতে অভ্যস্ত হয়। তাহেরের জীবনাবসান তার মনে গভীর দাগ ফেলে যায়- এতটাই যে ভারতীয় হাই কমিশনার সমর সেনকে অপহরণের পরিকল্পনাকে ‘সাহসী’ বলে ‍উপন্যাসে প্রশংসা করা হয়েছে। আমার ধারণা, হুমায়ূনের এই ব্যক্তিগত পটভূমি এই উপন্যাসের চরিত্র ও ঘটনার উপস্থাপনে তাকে প্রভাবান্বিত করেছে।<br><br> প্রথম আখ্যানেই আমরা পরিচিত হুমায়ূন আহমেদকে পাই। চরিত্রের খেয়ালিপনা, সংলাপের সংঘাত, ঘটনার আকস্মিকতা ও কার্যকারণহীনতা আমাদের সবসময়ে রহস্যময়তার দিকে আকর্ষণ করে। দ্বিতীয় আখ্যানের ঐতিহাসিকতা প্রমাণের জন্যে হুমায়ূন বইপত্র এবং মামলার কাগজপত্রের শরণাপন্ন হয়েছে। তবে তারপরও তথ্যগত ত্রুটি রয়ে গেছে। কাহিনি বলা থামিয়ে লেখক কখনো তারিখ দিয়ে মোটা দাগে ঘটনার বিবরণ লিখে গেছে। শেষে এক লাফে ছ বছর সময় পেরিয়ে উপসংহারে পৌঁছেছে।<br><br> এরই মধ্যে ছড়িয়ে আছে হুমায়ূনের স্বভাবসিদ্ধ এপিগ্রাম। সামান্য নমুনা দিই :<br> মানুষ এবং পশু শুধু যে বন্ধু খোঁজে তা না, তার প্রভুও খোঁজে।<br> এই পৃথিবীতে মূল্যবান শুধু মানুষের জীবন, আর সবই মূল্যহীন।<br> কিছু বিদ্যা মানুষের ভেতর থাকে। সে নিজেও তা জানে না।<br> যে লাঠি দিয়ে অন্ধ মানুষ পথ চলে, সেই লাঠি দিয়ে মানুষও খুন করা যায়।<br> মানবজাতির স্বভাব হচ্ছে সে সত্যের চেয়ে মিথ্যার আশ্রয়ে নিজেকে নিরাপদ মনে করে।<br><br> সমালোচক যা-ই বলুক না কেন, আমি জানি, হুমায়ূন আহমেদের অন্য বইয়ের মতো দেয়ালও পাঠকের সমাদর লাভ করবে।<br><br> আনিসুজ্জামান<br> বাংলা বিভাগ<br> ঢাকা বিশ্ববিদ্যালয় </div>', 'humayn ahmed book', 'humayn ahmed book', '[\"\\u09b8\\u09ae\\u0995\\u09be\\u09b2\\u09c0\\u09a8 \\u0989\\u09aa\\u09a8\\u09cd\\u09af\\u09be\\u09b8\",\"\\u0990\\u09a4\\u09bf\\u09b9\\u09be\\u09b8\\u09bf\\u0995\",\"\\u099c\\u09a8\\u09aa\\u09cd\\u09b0\\u09bf\\u09df\",\"\\u09ae\\u09c1\\u0995\\u09cd\\u09a4\\u09bf\\u09af\\u09c1\\u09a6\\u09cd\\u09a7\\u09ad\\u09bf\\u09a4\\u09cd\\u09a4\\u09bf\\u0995\",\"\\u09a5\\u09cd\\u09b0\\u09bf\\u09b2\\u09be\\u09b0\"]', 198, '2014-11-01', 0, '9789845021272', 'upload/book-images/deal.jpg', 0, 0, 0, '2025-01-19 00:22:33', '2025-02-09 10:07:23'),
(2, 1, 2, 2, 1, 1, 1, 'ময়ূরাক্ষী', 'HMA001', 200, 150, 25, 10, 1, 'ময়ূরাক্ষী নদীকে একবারই আমি স্বপ্নে দেখি। নদীটা আমার মনের ভেতর পুরোপুরি গাঁথা হয়ে যায়। অবাক হয়ে লক্ষ করি কোথাও বসে একটু চেষ্টা করলেই নদীটা আমি দেখতে ...', '<div class=\"summary-description active\" id=\"js--summary-description\"> <b>‘ময়ূরাক্ষী’ ফ্লাপে লেখা কথাঃ</b> ময়ূরাক্ষী নদীকে একবারই আমি স্বপ্নে দেখি। নদীটা আমার মনের ভেতর পুরোপুরি গাঁথা হয়ে যায়। অবাক হয়ে লক্ষ করি কোথাও বসে একটু চেষ্টা করলেই নদীটা আমি দেখতে পাই। তারজন্যে আমাকে কোনো কষ্ট করতে হয় না। চোখ বন্ধ করতে হয় না, কিছু না। একবার নদীটা বের করে আনতে পারলে সময় কাটানো কোনো সমস্যা নয়। ঘন্টার পর ঘন্টা আমি নদীর তীরে হাঁটি। নদীর হিম শীতল জলে পা ডুবিয়ে বসি। শরীর জুড়িয়ে যায়। ঘূঘুর ডাকে চোখ ভিজে ওঠে। </div>', 'dfsdf', 'dsfsdf', '[\"\\u09b9\\u09bf\\u09ae\\u09c1 \\u09b8\\u09bf\\u09b0\\u09bf\\u099c\",\"\\u0989\\u09aa\\u09a8\\u09cd\\u09af\\u09be\\u09b8\",\"\\u09b9\\u09c1\\u09ae\\u09be\\u09df\\u09c1\\u09a8 \\u0986\\u09b9\\u09ae\\u09c7\\u09a6\"]', 79, '2021-01-01', 0, '9844125227', 'upload/book-images/mourakki.gif', 0, 0, 0, '2025-01-19 00:39:02', '2025-01-19 00:39:02'),
(3, 1, 1, 3, 2, 1, 0, 'পথের পাঁচালী', 'BPS0001', 300, 235, 22, 10, 1, '‘পথের পাঁচালী\'-র সত্যকার বিষয়বস্তু অপুর ‘মনের আনন্দের প্রগতির ইতিহাস, তার ক্রমবর্ধমান চেতনার ইতিহাস।” এ উপন্যাসের মধ্যমণি অপু। তার উপর না...', '<div class=\"summary-description active\" id=\"js--summary-description\"> <b>\"পথেরপাঁচালী\" বইয়ের ভূমিকার লেখা: <br></b> ‘পথের পাঁচালী\'-র সত্যকার বিষয়বস্তু অপুর ‘মনের আনন্দের প্রগতির ইতিহাস, তার ক্রমবর্ধমান চেতনার ইতিহাস।” এ উপন্যাসের মধ্যমণি অপু। তার উপর নানা প্রভাব কার্যকরী হয়েছে—পিতামাতার প্রভাব, ভগিনী দুর্গার প্রভাব, পাড়া-প্রতিবেশীদের প্রভাব, এবং সর্বোপরি পল্লীপ্রকৃতির প্রভাব। ফলে তার মন ধীরে ধীরে অপরিণতি থেকে বিবর্তিত হয়ে একটা বিশিষ্ট রূপ ও রঙ অর্জন করেছে, এবং এই বিবর্তনের প্রক্রিয়াটি আনন্দের মাধ্যমেই সাধিত হয়েছে। শিশু ও বালক অপুর এই মানসিক বিবর্তনের কাহিনী পথের পাঁচালী’র প্রধান ও প্রায় একমাত্র উপজীব্য। </div>', 'fsdfs', 'dfssf', '[\"\\u09aa\\u09b6\\u09cd\\u099a\\u09bf\\u09ae\\u09ac\\u0999\\u09cd\\u0997\\u09c7\\u09b0 \\u09ac\\u0987\",\"\\u099a\\u09bf\\u09b0\\u09be\\u09df\\u09a4\",\"\\u099c\\u09a8\\u09aa\\u09cd\\u09b0\\u09bf\\u09df\",\"\\u0997\\u09cd\\u09b0\\u09be\\u09ae\\u09c0\\u09a8 \\u099c\\u09c0\\u09ac\\u09a8\"]', 192, '2014-01-01', 0, '9846580169', 'upload/book-images/pother .jpg', 0, 0, 0, '2025-01-19 00:49:13', '2025-01-19 00:49:13'),
(4, 2, 8, 4, 1, 1, 0, 'ছোটদের সেরা গল্প', 'hmssg', 250, 188, 25, 33, 1, 'শিশু-কিশোরদের জন্য হুমায়ূন আহমেদ দু’হাতে না লিখলেও প্রচুর লিখেছেন। মজার মজার সব লেখা। রহস্য, এডভেঞ্চার, ভৌতিক গল্প, সায়েন্স ফিকশান কোন কিছুই বাদ দেন নি। তাঁর স...', '<div class=\"summary-description active\" id=\"js--summary-description\"> <b>প্রকাশকের কথা</b><br> শিশু-কিশোরদের জন্য হুমায়ূন আহমেদ দু’হাতে না লিখলেও প্রচুর লিখেছেন। মজার মজার সব লেখা। রহস্য, এডভেঞ্চার, ভৌতিক গল্প, সায়েন্স ফিকশান কোন কিছুই বাদ দেন নি। তাঁর সেই সব লেখা থেকে বাছাই করা কিছু গল্প আমরা প্রকাশ করতে চেয়েছিলাম। হুমায়ূন আহমেদ আমাদের প্রস্তাবে রাজি হলেন। যখন তাঁদের বললাম গল্প বাছাই করে দিতে, তখন তিনি বললেন- আমি কি করে বলব কোনটা সেরা গল্প, কোনটা নয়? আপনি বাচ্চাদের জিজ্ঞাস করুন। ওদের মতামত দিয়ে সেরা গল্প বাছাই করুন। এই সংকলনে তাই করা হয়েছে। বেশির ভাগ ক্ষুদে পাঠক যে সব গল্পকে সেরা হিসেবে চিহ্নিত করেছে সেই সবই রাখা হয়েছে। গল্পের তালিকা লেখককে দেখানো হলে তিনি ভুরু কুঁচকে কিছুক্ষণ তাকিয়ে থেকে বললেন- আমার কিছু ভাল গল্প বাচ্চারা বাদ দিয়েছে, এর কারণ কি? আমি বললাম, আপনার পছন্দের গল্প কি ঢুকিয়ে দেব? তিনি বললেন, অবশ্যই না। শিশুদের মতে কোনটি সেরা তা আমরা ঠিক করে ওদের উপর চাপিয়ে দেব না। ওরাই ওদেরটা ঠিক করবে। তাই করা উচিত। আমরা এই সংকলনে তা করেছি।<br><br> <b>আহমেদ মাহমুদুল হক</b><br> <b>সূচিপত্র</b><br>*বোকা দৈত্য<br>*লোকটি<br>*নীল হাতি<br>*গোবর বাবু<br>*রুঁরুঁর গল্প<br>*বড় মামা এবং রাজকুমারী সুবর্ণ রেখা<br>*কানী ডাইনী<br>*আলাউদ্দিনের চেরাগ<br>*একটি ভয়ংকর অভিযানের গল্প<br>*রাণী কলাবতী </div>', 'ছোটদের সেরা গল্প', 'ছোটদের সেরা গল্প', '[\"\\u0997\\u09b2\\u09cd\\u09aa\",\"\\u09b9\\u09c1\\u09ae\\u09be\\u09df\\u09c1\\u09a8 \\u0986\\u09b9\\u09ae\\u09c7\\u09a6\\u09c7\\u09b0 \\u0997\\u09b2\\u09cd\\u09aa\",\"\\u099c\\u09a8\\u09aa\\u09cd\\u09b0\\u09bf\\u09df\"]', 103, '2015-12-01', 0, '9844100518', 'upload/book-images/ছোটদের সেরা গল্প.png', 0, 0, 0, '2025-01-29 22:32:06', '2025-01-29 23:08:52'),
(5, 2, 8, 5, 2, 1, 0, 'তালনবমী', 'tb001', 200, 130, 35, 44, 1, 'সূচি\r\n\r\n\r\n তালনবমী ..........................................০৭\r\n\r\n রঙ্কিণীদেবীর খড়্গ .................................১৫\r\n\r\n মেডেল ..........................', '<div class=\"summary-description active\" id=\"js--summary-description\"><p>সূচি</p><p><br></p><p> তালনবমী ..........................................০৭</p><p> রঙ্কিণীদেবীর খড়্গ .................................১৫</p><p> মেডেল .............................................২৫</p><p> মসলাভূত ...........................................৪০</p><p> বামা ................................................৪৮</p><p> বামাচরণের গুপ্তধন প্রাপ্তি ..........................৫৭</p><p> অরণ্যে .............................................৬৭</p><p> গঙ্গাধরের বিপদ ...................................৭৭</p><p> রাজপুত্র ............................................৮৪</p><p> চাউল ...............................................৯০</p></div>', 'তালনবমী', 'তালনবমী', '[\"\\u0997\\u09b2\\u09cd\\u09aa\",\"\\u09ac\\u09bf\\u09ad\\u09c2\\u09a4\\u09bf\\u09ad\\u09c2\\u09b7\\u09a3 \\u09ac\\u09a8\\u09cd\\u09a6\\u09cd\\u09af\\u09cb\\u09aa\\u09be\\u09a7\\u09cd\\u09af\\u09be\\u09df\\u09c7\\u09b0 \\u0997\\u09b2\\u09cd\\u09aa\",\"\\u099c\\u09a8\\u09aa\\u09cd\\u09b0\\u09bf\\u09df\",\"\\u099a\\u09bf\\u09b0\\u09be\\u09df\\u09a4 \\u0997\\u09b2\\u09cd\\u09aa\"]', 96, '2024-02-01', 1, '9789849559894', 'upload/book-images/Talonbomi-Bibhutibhusan_Bandopadhyay.jpg', 0, 0, 0, '2025-01-29 23:32:32', '2025-01-29 23:32:32'),
(6, 2, 8, 7, 3, 1, 0, 'শ্রেষ্ঠ গল্প', 'sjg99', 300, 258, 14, 25, 1, 'ছােটদের মহলে শিবরাম চক্রবর্তীর এমন সার্বিক সাফল্যের মূলমন্ত্রটি কী? তিনি বলেছেন : আমার লেখায় ছােটদের কখনই আমি ছােট বলে ধরিনি, অবােধ শ...', '<div class=\"summary-description active\" id=\"js--summary-description\"><b>\"শ্রেষ্ঠ গল্প\" বইটির সম্পর্কে কিছু কথা:<br></b> ছােটদের মহলে শিবরাম চক্রবর্তীর এমন সার্বিক সাফল্যের মূলমন্ত্রটি কী? তিনি বলেছেন : আমার লেখায় ছােটদের কখনই আমি ছােট বলে ধরিনি, অবােধ শিশু বলে গণ্য করিনি কখনও। আমার সমকক্ষ বলেই ধরেছি তাদের। বয়স্ক বন্ধুর মতন বিবেচনা করেছি। তবু শিশু-কিশােরদের জগতে শিবরামের তুমুল জনপ্রিয়তার মূলে তিনজন আলঙ্কারিকের অবদানও কিন্তু সর্বদাই মনে রাখতে হবে। তাঁরা হলেন রেবতীভূষণ, শৈল চক্রবর্তী এবং প্রফুল্লচন্দ্র লাহিড়ী। এঁদের দক্ষ তুলির বলিষ্ঠ টান একটি গুরুদায়িত্ব পালন করেছে, সকলের সঙ্গে শিবরামের পরিচয় ঘটিয়ে দিয়েছে— নধরকান্তি চেহারা, উজ্জ্বল দুটি চোখ, সেই ব্যাকব্রাশ চুল, যার অগােছালাে একটি গুচ্ছ আনমনে অথবা অবহেলায় কানের পাশে লুটাচ্ছে। দীর্ঘকাল কোনও পত্র-পত্রিকায় শিবরামের কোনও ফটো দেখা যায়নি। ক্যামেরা দেখলে তার আতঙ্ক হত, পিছিয়ে যেতেন। ক্যামেরাধারী কোনও স্বজনও তাকে ফটো তুলতে রাজি করাতে পারেনি। তাই প্রিয় লেখকের ছবি তার ভক্ত পাঠকদের মনে মােটা দাগে দেগে মূর্ত করে তােলার জন্যে শিবরাম চক্রবর্তীর সঙ্গে শক্তিমান ওই তিন চিত্রশিল্পীও চিরস্মরণীয়। বস্তুত তাঁদের অলংকরণই যেন শিবরাম এবং তাঁর চরিত্রদের সম্পূর্ণতা দান করেছে।</div>', 'শ্রেষ্ঠ গল্প', 'শ্রেষ্ঠ গল্প', '[\"\\u09b6\\u09cd\\u09b0\\u09c7\\u09b7\\u09cd\\u09a0 \\u0997\\u09b2\\u09cd\\u09aa\",\"\\u09b6\\u09bf\\u09ac\\u09b0\\u09be\\u09ae \\u099a\\u0995\\u09cd\\u09b0\\u09ac\\u09b0\\u09cd\\u09a4\\u09c0\\u09b0 \\u0997\\u09b2\\u09cd\\u09aa\",\"\\u099a\\u09bf\\u09b0\\u09be\\u09df\\u09a4 \\u0997\\u09b2\\u09cd\\u09aa\"]', 286, '2018-04-02', 0, '9841802767', 'upload/book-images/660d65059_8225.jpg', 0, 0, 0, '2025-01-29 23:47:30', '2025-01-29 23:47:30'),
(7, 2, 9, 8, 4, 1, 0, 'সুরাইয়া', 'bsbs934', 280, 241, 14, 44, 1, 'শিবব্রত বর্মনের গল্পগুলোর চৌহদ্দি চিহ্নিত করা কঠিন। সোনার ডিম পাড়ে না এমন এক বিপন্ন হাঁসের পাশে অদৃশ্য এক দাবার বোর্ডে খেলতে বসে দুই তিক্ত প্রতিদ্বন্দ্বী আর তখনই মধ্যরাতে দোতলা বাসে মৃত...', '<div class=\"summary-description active\" id=\"js--summary-description\">শিবব্রত বর্মনের গল্পগুলোর চৌহদ্দি চিহ্নিত করা কঠিন। সোনার ডিম পাড়ে না এমন এক বিপন্ন হাঁসের পাশে অদৃশ্য এক দাবার বোর্ডে খেলতে বসে দুই তিক্ত প্রতিদ্বন্দ্বী আর তখনই মধ্যরাতে দোতলা বাসে মৃত্যু সাজিয়ে বসে থাকে এক মানসিক রোগী। এ গল্পসংকলন এক আশ্চর্য ভ্রমণ।</div>', 'সুরাইয়া', 'সুরাইয়া', '[\"\\u0997\\u09b2\\u09cd\\u09aa\",\"\\u09b6\\u09bf\\u09ac\\u09ac\\u09cd\\u09b0\\u09a4 \\u09ac\\u09b0\\u09cd\\u09ae\\u09a8\\u09c7\\u09b0 \\u0997\\u09b2\\u09cd\\u09aa\",\"\\u09b8\\u09ae\\u0995\\u09be\\u09b2\\u09c0\\u09a8 \\u0997\\u09b2\\u09cd\\u09aa\",\"\\u099c\\u09a8\\u09aa\\u09cd\\u09b0\\u09bf\\u09df\"]', 128, '2023-03-01', 0, '9789849654957', 'upload/book-images/Suraiya-Shibobroto_Bormon-538e7-230735.jpg', 0, 0, 0, '2025-01-30 00:01:33', '2025-01-30 00:01:33'),
(8, 2, 9, 2, 5, 1, 0, 'ইচ্ছা পূরণ', 'iccapuron123', 135, 116, 14, 25, 1, 'টিকটিকিকে ধরার চেষ্টা করলে তাদের লেজটা খুলে আসে এবং তিড়িং তিড়িং করে নড়তে থাকে। সেটা দেখে টুটুল এতো মজা পেলো যে সে একটা একটা করে সবগুলো টিকটিকির ...', '<div class=\"summary-description active\" id=\"js--summary-description\"><b>‘ইচ্ছা পূরণ’ বইয়ের ফ্ল্যাপের কথাঃ</b> টিকটিকিকে ধরার চেষ্টা করলে তাদের লেজটা খুলে আসে এবং তিড়িং তিড়িং করে নড়তে থাকে। সেটা দেখে টুটুল এতো মজা পেলো যে সে একটা একটা করে সবগুলো টিকটিকির লেজ খসিয়ে ফেলল, তাদের বাসার কোনো টিকটিকির এখন কোনো লেজ নাই।<br> <b>‘ইচ্ছা পূরণ’ বইটির সামারিঃ</b><br> ২০১৭ সালের বইমেলায় প্রকাশ হওয়া ১০ বছর বয়েসি ছেলে বাচ্চাকে নিয়ে লেখা বইটি কিছুটা ছোটদের অ্যাডভেঞ্চার এর মত। এখানে টুটুল প্রতিমুহুর্তেই কিছু না কিছু এক্সপেরিমেন্ট করে এবং নিশ্চিত ভাবে সেটা ভুল হয় কিন্তু তাই বলে তাঁর কাজ থেমে থাকে না। বইটি রঙিন ছবিসহ এবং বাচ্চাদের উত্তাল আনন্দের মধ্যে বড় করে তোলার দিকটি লেখক জাফর ইকবাল সুন্দর করে তুলে এনেছেন। </div>', 'ইচ্ছা পূরণ', 'ইচ্ছা পূরণ', '[\"\\u0997\\u09b2\\u09cd\\u09aa\",\"\\u09ae\\u09c1\\u09b9\\u09be\\u09ae\\u09cd\\u09ae\\u09a6 \\u099c\\u09be\\u09ab\\u09b0 \\u0987\\u0995\\u09ac\\u09be\\u09b2\\u09c7\\u09b0 \\u0997\\u09b2\\u09cd\\u09aa\",\"\\u09b8\\u09ae\\u0995\\u09be\\u09b2\\u09c0\\u09a8 \\u0997\\u09b2\\u09cd\\u09aa\"]', 16, '2017-01-02', 0, '9789844323360', 'upload/book-images/faffe454dcb4_129446.jpg', 0, 0, 0, '2025-01-30 00:15:56', '2025-01-30 00:15:56'),
(9, 2, 10, 9, 6, 1, 0, 'বড় যদি হতে চাও', 'mjabjhc00', 360, 180, 50, 46, 1, 'বই থেকে জানা যাবে প্রথমত শত শত মনিষীর নাম—তাঁদের জীবনের টুকরো টুকরো গল্প—ইলম হাসিলে কষ্ট মুজাহাদার বয়ান—অসামান্য ধৈর্য্য ও ইস্পাত কঠিন দৃঢ়তা—উসতাদের প্রতি অসামান্য ভক্তি ও শ্রদ্ধাবোধ। মুসলিম উম্মাহর আকাশ আলো করা সেইসব', '<p class=\"review-text js--review-content active\">বইটি মূলত মাদরাসার তালিবে ইলমদের উদ্দেশ্য করে লেখা। যদিও আমার পড়াশোনা আগাগোড়া জেনারেল লাইনে এবং বইটি কিনেছিলাম লেখক ও তাঁর লেখনীর সাথে পরিচয়ের খাতিরে!<br><br>বই থেকে জানা যাবে প্রথমত শত শত মনিষীর নাম—তাঁদের জীবনের টুকরো টুকরো গল্প—ইলম হাসিলে কষ্ট মুজাহাদার বয়ান—অসামান্য ধৈর্য্য ও ইস্পাত কঠিন দৃঢ়তা—উসতাদের প্রতি অসামান্য ভক্তি ও শ্রদ্ধাবোধ। মুসলিম উম্মাহর আকাশ আলো করা সেইসব তারকা পুরুষদের নিরবচ্ছিন্ন অধ্যবসায় পাঠককে আরো ভাবুক ও পড়ুয়া হতে উৎসাহিত করবে ইনশা আল্লাহ।<br><br>বই পড়তে গিয়ে আপনি একঘেয়েমি বোধ করবেন না। লেখক সে সুযোগ রাখেননি—মনে হবে একটানে শেষ করে তবেই উঠবো! ঘুরেফিরে এসেছে ইলম পিপাসু সাহাবীদের (রাঃ) দের আলোচনা—মহান চার ইমাম ও তাঁদের ছাত্রদের কথা। এছাড়াও রয়েছে নিকট অতীতের আল্লামা আনওয়ার শাহ কাশমিরী ও মাওলানা আবুল কালাম আজাদের কথা।<br><br>সুতরাং পাঠক ঘুরে আসবেন মক্কা, মদিনা, কুফা, বাগদাদ, সিরিয়া। ইলম অনুসন্ধানী এই মানুষদের জীবনকথা পাঠককে মনে করিয়ে দিবে ইসলামের প্রথম কথা ছিলো—পড়।<br><br>শেষ কথা-<br><br>বইয়ের শুরুতে মাওলানা উবায়দুর রহমান খান নদভী বইটির ভূয়সী প্রশংসা করে বলেন, এটি আনুষ্ঠানিকভাবে মাদরাসায় পাঠ্য হওয়ার মতো।<br><br>রাহনুমা প্রকাশনী খুউব যত্ন করে বইটি ছেপেছে। তাদের সার্বিক উন্নতি কামনা করি।</p>', 'বড় যদি হতে চাও', 'বড় যদি হতে চাও', '[\"\\u0997\\u09b2\\u09cd\\u09aa\",\"\\u0987\\u09b8\\u09b2\\u09be\\u09ae\\u09bf \\u0997\\u09b2\\u09cd\\u09aa\",\"\\u09ae\\u09a8\\u09bf\\u09b7\\u09c0\\u09a6\\u09c7\\u09b0 \\u0997\\u09b2\\u09cd\\u09aa\"]', 216, '2017-01-02', 0, '9789849221296', 'upload/book-images/116d2bd54_143032.jpg', 0, 0, 0, '2025-01-30 00:32:06', '2025-01-30 00:32:06'),
(11, 1, 12, 11, 8, 1, 0, 'আশিয়ানী', 'asiani324', 1200, 720, 40, 9, 1, 'সেভিদোনিয়া রাজ্যের রাজকুমারী আশিয়ানী আরনোমিয়েল এক রহস্যের পেছনে ছুটতে ছুটতে পৌঁছে যায় এমন একটা জায়গায় যা তার জীবনাচরণকে বদলে দেয় চিরদিনের জন্য! সেই সাথে বদলে যায় আরও একটি জীবন। এদি..', '<p><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">সেভিদোনিয়া রাজ্যের রাজকুমারী আশিয়ানী আরনোমিয়েল এক রহস্যের পেছনে ছুটতে ছুটতে পৌঁছে যায় এমন একটা জায়গায় যা তার জীবনাচরণকে বদলে দেয় চিরদিনের জন্য! সেই সাথে বদলে যায় আরও একটি জীবন। এদিকে সেভিদোনিয়ায় হঠাৎ করেই একের-পর-এক অযাচিত ঘটনা ঘটে চলেছে, যা নিয়ে ক্রমেই উদ্বিগ্ন হয়ে উঠছে রাজা আরসালান আরনোমিয়েল। রাজপ্রাসাদের প্রধান সেনাধ্যক্ষ আরহাম মেনদারকে সাথে নিয়ে সেইসব সমস্যা সমাধানের চেষ্টা করতে থাকেন তিনি, যার সাথে একসময় জড়িয়ে ফেলা হয় আশিয়ানীকেও। সিংহাসনের পরবর্তী উত্তরাধিকারী হিসেবে তার ওপরও চাপ বাড়তে থাকে। অভিজাতদের সাথে রাজকুমারীর নীরব যুদ্ধ প্রকাশ্যে চলে আসে হঠাৎ! ইলদারাম পর্বতমালার উত্তরাংশ কেন হঠাৎ উত্তপ্ত হয়ে উঠলো? রাজধানী সোভিয়ানেই বা এমন অযাচিত ঘটনা কারা ঘটালো? প্রাচীন জাদুকর সংঘের একজন প্রকৃতিসাধিকা ঠিক কিসের আশংকা করছে? আরনোমিয়েল সেনাবাহিনী এবং রাখশামান সেনাদলকে কেন হঠাৎ করে তলব করার প্রয়োজন পড়লো? হরিৎসেনারাই বা কী নিয়ে কাজ করছে? রাজবৃক্ষ আরনীমিশিল এর কী ভূমিকা রয়েছে আরনোমিয়েল রাজবংশের উত্থানের পেছনে? আর সবচেয়ে বড়ো প্রশ্ন, এসব ঘটনা আশিয়ানীর সাথেই বা কতটুকু জড়িত? এসব প্রশ্নের উত্তর রয়েছে প্রাচীন রহস্য, রাজনৈতিক কূটকৌশল, সমরকৌশল, বিশ্বাসঘাতকতা এবং নিয়তির মিশেলে গঠিত বিশাল প্রেক্ষাপটের ফ্যান্টাসি উপন্যাস আশিয়ানী-তে।</span></p>', 'আশিয়ানী', 'আশিয়ানী', '[\"\\u09ab\\u09cd\\u09af\\u09be\\u09a8\\u09cd\\u099f\\u09b8\\u09bf \\u0989\\u09aa\\u09a8\\u09cd\\u09af\\u09be\\u09b8\"]', 665, '2022-01-23', 0, 'dfdfgdfgfdg', 'upload/book-images/2303551.jpg', 0, 0, 0, '2025-02-22 13:44:22', '2025-02-22 13:44:22'),
(12, 1, 13, 11, 9, 1, 0, 'মহাকাল', 'mohakal324', 1000, 600, 40, 10, 1, 'মহারাজ সাতকর্ণী সাতবাহন রাজ্যের সম্রাট। লোকে বলে, তিনিই এই বংশের সবচেয়ে যোগ্য রাজা। রাজ্য চারিদিকে বেড়ে এখন পুরো মধ্য ভারতই তার আয়ত্তে এসে গেছে। কিন্তু সমস্যা হয়..', '<p><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">কাহিনি সংক্ষেপ</span><br style=\"--tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-pan-x: ; --tw-pan-y: ; --tw-pinch-zoom: ; --tw-scroll-snap-strictness: proximity; --tw-gradient-from-position: ; --tw-gradient-via-position: ; --tw-gradient-to-position: ; --tw-ordinal: ; --tw-slashed-zero: ; --tw-numeric-figure: ; --tw-numeric-spacing: ; --tw-numeric-fraction: ; --tw-ring-inset: ; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; --tw-blur: ; --tw-brightness: ; --tw-contrast: ; --tw-grayscale: ; --tw-hue-rotate: ; --tw-invert: ; --tw-saturate: ; --tw-sepia: ; --tw-drop-shadow: ; --tw-backdrop-blur: ; --tw-backdrop-brightness: ; --tw-backdrop-contrast: ; --tw-backdrop-grayscale: ; --tw-backdrop-hue-rotate: ; --tw-backdrop-invert: ; --tw-backdrop-opacity: ; --tw-backdrop-saturate: ; --tw-backdrop-sepia: ; --tw-contain-size: ; --tw-contain-layout: ; --tw-contain-paint: ; --tw-contain-style: ; border: 0px solid rgb(229, 231, 235); margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px; user-select: initial !important;\"><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">মহারাজ সাতকর্ণী সাতবাহন রাজ্যের সম্রাট। লোকে বলে, তিনিই এই বংশের সবচেয়ে যোগ্য রাজা। রাজ্য চারিদিকে বেড়ে এখন পুরো মধ্য ভারতই তার আয়ত্তে এসে গেছে। কিন্তু সমস্যা হয়ে দাঁড়িয়েছে অন্য জায়গায়। পুবের পৃথিবী থেকে লোকজন আসছে ভারতে বাণিজ্য করতে। তিনিও সেই বাণিজ্যে অংশ নিয়েছেন। কিন্তু তার বন্দরগুলোতে ভিনদেশী জাহাজগুলো আসছে না। খা খা করছে তার বন্দরগুলো। এই সমস্যার সমাধান করতেই হবে। লক্ষ্মী বিদায় নিলে রাজা আর রাজ্য যে শ্রী হারিয়ে ফেলবে। নিজের মন্ত্রীর সাথে সেই পরিকল্পনাই করতে বসেছেন তিনি।</span><br style=\"--tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-pan-x: ; --tw-pan-y: ; --tw-pinch-zoom: ; --tw-scroll-snap-strictness: proximity; --tw-gradient-from-position: ; --tw-gradient-via-position: ; --tw-gradient-to-position: ; --tw-ordinal: ; --tw-slashed-zero: ; --tw-numeric-figure: ; --tw-numeric-spacing: ; --tw-numeric-fraction: ; --tw-ring-inset: ; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; --tw-blur: ; --tw-brightness: ; --tw-contrast: ; --tw-grayscale: ; --tw-hue-rotate: ; --tw-invert: ; --tw-saturate: ; --tw-sepia: ; --tw-drop-shadow: ; --tw-backdrop-blur: ; --tw-backdrop-brightness: ; --tw-backdrop-contrast: ; --tw-backdrop-grayscale: ; --tw-backdrop-hue-rotate: ; --tw-backdrop-invert: ; --tw-backdrop-opacity: ; --tw-backdrop-saturate: ; --tw-backdrop-sepia: ; --tw-contain-size: ; --tw-contain-layout: ; --tw-contain-paint: ; --tw-contain-style: ; border: 0px solid rgb(229, 231, 235); margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px; user-select: initial !important;\"><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">ঠিক সেই সময়ে যে রাজা ভারতে চুটিয়ে বাণিজ্য করছেন, তার নাম নাহাপনা। পশ্চিম শক রাজ্যের এই রাজা নিজের বাণিজ্য নিয়ে সন্তুষ্ট। তবে নিশ্চিন্ত থাকার উপায় নেই। তিনি জানেন, মধ্য ভারত অধিকার করে সাতবাহন রাজ্যের সীমা এখন তার রাজ্যের উপকণ্ঠে চলে এসেছে। আর তার এই ছোট কিন্তু অতুল সমৃদ্ধ রাজ্য অবশ্যই সাতকর্ণীর শকুন নজর এড়াবে না। তাই শত্রু আঘাত করার আগেই শত্রুকে পাকে ফেলতে হবে। নিজের মন্ত্রীর সাথে তিনিও ব্যস্ত সেই উপায় নির্ধারণে।</span><br style=\"--tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-pan-x: ; --tw-pan-y: ; --tw-pinch-zoom: ; --tw-scroll-snap-strictness: proximity; --tw-gradient-from-position: ; --tw-gradient-via-position: ; --tw-gradient-to-position: ; --tw-ordinal: ; --tw-slashed-zero: ; --tw-numeric-figure: ; --tw-numeric-spacing: ; --tw-numeric-fraction: ; --tw-ring-inset: ; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; --tw-blur: ; --tw-brightness: ; --tw-contrast: ; --tw-grayscale: ; --tw-hue-rotate: ; --tw-invert: ; --tw-saturate: ; --tw-sepia: ; --tw-drop-shadow: ; --tw-backdrop-blur: ; --tw-backdrop-brightness: ; --tw-backdrop-contrast: ; --tw-backdrop-grayscale: ; --tw-backdrop-hue-rotate: ; --tw-backdrop-invert: ; --tw-backdrop-opacity: ; --tw-backdrop-saturate: ; --tw-backdrop-sepia: ; --tw-contain-size: ; --tw-contain-layout: ; --tw-contain-paint: ; --tw-contain-style: ; border: 0px solid rgb(229, 231, 235); margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px; user-select: initial !important;\"><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">দক্ষিণ ভারত তিন রাজ্যে বিভক্ত। চেরা, চোলা আর পাণ্ড্য। তিন রাজ্যই একে অপরের প্রতি বিদ্বেষ পোষণ করে আসছে প্রাচীন কাল থেকে। তবে এখন চেরা রাজ্যের অবস্থা ভালো। তার বন্দর মুঝিরিস ব্যবসায় অনেক ভালো করছে। তারও ভয় মহারাজ সাতকর্ণীকে। চোলা রাজ্যে এসেছেন নতুন রাজা। তাকেও থাকতে হচ্ছে ভেতরের আর বাইরের শত্রুর ভয়ে। পাণ্ড্য রাজা বাণিজ্যিক চুক্তি করেছেন চেরাদের সাথে, তবে তিনিও চাচ্ছেন না অন্য কারো দয়ায় বেঁচে থাকতে।</span><br style=\"--tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-pan-x: ; --tw-pan-y: ; --tw-pinch-zoom: ; --tw-scroll-snap-strictness: proximity; --tw-gradient-from-position: ; --tw-gradient-via-position: ; --tw-gradient-to-position: ; --tw-ordinal: ; --tw-slashed-zero: ; --tw-numeric-figure: ; --tw-numeric-spacing: ; --tw-numeric-fraction: ; --tw-ring-inset: ; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; --tw-blur: ; --tw-brightness: ; --tw-contrast: ; --tw-grayscale: ; --tw-hue-rotate: ; --tw-invert: ; --tw-saturate: ; --tw-sepia: ; --tw-drop-shadow: ; --tw-backdrop-blur: ; --tw-backdrop-brightness: ; --tw-backdrop-contrast: ; --tw-backdrop-grayscale: ; --tw-backdrop-hue-rotate: ; --tw-backdrop-invert: ; --tw-backdrop-opacity: ; --tw-backdrop-saturate: ; --tw-backdrop-sepia: ; --tw-contain-size: ; --tw-contain-layout: ; --tw-contain-paint: ; --tw-contain-style: ; border: 0px solid rgb(229, 231, 235); margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px; user-select: initial !important;\"><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">এসব রাজরাজড়ার ঝামেলায় অসিতের কোন সম্পর্ক নেই। সে এক সাধারণ কিশোর। নিজের খেলা দেখানোর বানর আর গ্রামের অন্য মানুষদের কখনো আদর আর কখনো শাসনে কেটে যাচ্ছে তার জীবন। বনের ধারে একটা কেবল কুটির, আর কিছু খেলা দেখানোর সামগ্রী, এই তার সম্বল। তারপর আচমকা একদিন নতুন এক সম্পত্তি হাতে চলে এলো তার। নতুন সম্পত্তির সাথে এলো নতুন সম্ভাবনা, আর সেই সাথে এলো নতুন বিপদও। জীবন বদলে গেলো তার।</span><br style=\"--tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-pan-x: ; --tw-pan-y: ; --tw-pinch-zoom: ; --tw-scroll-snap-strictness: proximity; --tw-gradient-from-position: ; --tw-gradient-via-position: ; --tw-gradient-to-position: ; --tw-ordinal: ; --tw-slashed-zero: ; --tw-numeric-figure: ; --tw-numeric-spacing: ; --tw-numeric-fraction: ; --tw-ring-inset: ; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; --tw-blur: ; --tw-brightness: ; --tw-contrast: ; --tw-grayscale: ; --tw-hue-rotate: ; --tw-invert: ; --tw-saturate: ; --tw-sepia: ; --tw-drop-shadow: ; --tw-backdrop-blur: ; --tw-backdrop-brightness: ; --tw-backdrop-contrast: ; --tw-backdrop-grayscale: ; --tw-backdrop-hue-rotate: ; --tw-backdrop-invert: ; --tw-backdrop-opacity: ; --tw-backdrop-saturate: ; --tw-backdrop-sepia: ; --tw-contain-size: ; --tw-contain-layout: ; --tw-contain-paint: ; --tw-contain-style: ; border: 0px solid rgb(229, 231, 235); margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px; user-select: initial !important;\"><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">এক অভিশপ্ত পুরুষ রাতের অন্ধকারে ঘুরে বেড়াচ্ছে নানা মন্দিরে। দিনে কখনোই সে ঘর থেকে বের হয় না। নিত্য প্রার্থনা করে চলেছে সে, কিন্তু কিছুতেই নিজের শাপের হাত থেকে মুক্ত হতে পারছে না। তবু চেষ্টা করে চলেছে, মহাকাল কি তাকে দয়া করবে? তার শাস্তির মেয়াদ কি শেষ হবে?</span><br style=\"--tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-pan-x: ; --tw-pan-y: ; --tw-pinch-zoom: ; --tw-scroll-snap-strictness: proximity; --tw-gradient-from-position: ; --tw-gradient-via-position: ; --tw-gradient-to-position: ; --tw-ordinal: ; --tw-slashed-zero: ; --tw-numeric-figure: ; --tw-numeric-spacing: ; --tw-numeric-fraction: ; --tw-ring-inset: ; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; --tw-blur: ; --tw-brightness: ; --tw-contrast: ; --tw-grayscale: ; --tw-hue-rotate: ; --tw-invert: ; --tw-saturate: ; --tw-sepia: ; --tw-drop-shadow: ; --tw-backdrop-blur: ; --tw-backdrop-brightness: ; --tw-backdrop-contrast: ; --tw-backdrop-grayscale: ; --tw-backdrop-hue-rotate: ; --tw-backdrop-invert: ; --tw-backdrop-opacity: ; --tw-backdrop-saturate: ; --tw-backdrop-sepia: ; --tw-contain-size: ; --tw-contain-layout: ; --tw-contain-paint: ; --tw-contain-style: ; border: 0px solid rgb(229, 231, 235); margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px; user-select: initial !important;\"><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">বিন্দুমাত্র সম্পর্কহীন বিন্দুগুলো এক সময় এসে মিলে গেলো এক বিন্দুতে। মহাকাল দেখাতে শুরু করলো নিজের খেলা।</span></p>', 'মহাকাল', 'মহাকাল', '[\"\\u09aa\\u09c7\\u09d7\\u09b0\\u09be\\u09a8\\u09bf\\u0995\"]', 608, '2023-02-23', 0, 'dsfsdfsdf453543543545', 'upload/book-images/Mohakal-Dibakor_Das-5f0ae-287164.jpg', 0, 0, 0, '2025-02-22 13:52:41', '2025-02-22 13:52:41'),
(13, 4, 14, 12, 10, 1, 0, 'বহে জলবতী ধারা- ১ম খণ্ড', 'bohdf34', 360, 310, 14, 10, 1, 'জীবন বহমান, জলবতী ধারার মতোই; কিন্তু মানুষের। জীবনের একটা আশ্চর্য ব্যাপার হলো, সে শুধু বহেই চলে না, নিজের গতিপথের দিকে তাকাতেও পারে, একই সঙ্গে একটা। জীবন যাপন করা আর সেই যাপিত জীবনের স্মৃতিরচনা করা সম্ভব হয় মানুষের পক্ষে। আর সেই জীবনটা যদি হয় ....', '<p><span style=\"--tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-pan-x: ; --tw-pan-y: ; --tw-pinch-zoom: ; --tw-scroll-snap-strictness: proximity; --tw-gradient-from-position: ; --tw-gradient-via-position: ; --tw-gradient-to-position: ; --tw-ordinal: ; --tw-slashed-zero: ; --tw-numeric-figure: ; --tw-numeric-spacing: ; --tw-numeric-fraction: ; --tw-ring-inset: ; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; --tw-blur: ; --tw-brightness: ; --tw-contrast: ; --tw-grayscale: ; --tw-hue-rotate: ; --tw-invert: ; --tw-saturate: ; --tw-sepia: ; --tw-drop-shadow: ; --tw-backdrop-blur: ; --tw-backdrop-brightness: ; --tw-backdrop-contrast: ; --tw-backdrop-grayscale: ; --tw-backdrop-hue-rotate: ; --tw-backdrop-invert: ; --tw-backdrop-opacity: ; --tw-backdrop-saturate: ; --tw-backdrop-sepia: ; --tw-contain-size: ; --tw-contain-layout: ; --tw-contain-paint: ; --tw-contain-style: ; border: 0px solid rgb(229, 231, 235); margin: 0px; padding: 0px; font-weight: bolder; color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px; user-select: initial !important;\">\"বহে জলবতী ধারা- ১ম খণ্ড\" বইটিতে লেখা ফ্ল্যাপের কথাঃ</span><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">&nbsp;জীবন বহমান, জলবতী ধারার মতোই; কিন্তু মানুষের। জীবনের একটা আশ্চর্য ব্যাপার হলো, সে শুধু বহেই চলে না, নিজের গতিপথের দিকে তাকাতেও পারে, একই সঙ্গে একটা। জীবন যাপন করা আর সেই যাপিত জীবনের স্মৃতিরচনা করা সম্ভব হয় মানুষের পক্ষে। আর সেই জীবনটা যদি হয় আবদুল্লাহ আবু সায়ীদের মতো কর্মকীর্তিময় মানুষের, তাহলে তা হয়ে ওঠে আরও বর্ণাঢ্য, আরো বিরাট এক অনুভূতিময় আবেগদীপ্ত ব্যাপার।</span><br style=\"--tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-pan-x: ; --tw-pan-y: ; --tw-pinch-zoom: ; --tw-scroll-snap-strictness: proximity; --tw-gradient-from-position: ; --tw-gradient-via-position: ; --tw-gradient-to-position: ; --tw-ordinal: ; --tw-slashed-zero: ; --tw-numeric-figure: ; --tw-numeric-spacing: ; --tw-numeric-fraction: ; --tw-ring-inset: ; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; --tw-blur: ; --tw-brightness: ; --tw-contrast: ; --tw-grayscale: ; --tw-hue-rotate: ; --tw-invert: ; --tw-saturate: ; --tw-sepia: ; --tw-drop-shadow: ; --tw-backdrop-blur: ; --tw-backdrop-brightness: ; --tw-backdrop-contrast: ; --tw-backdrop-grayscale: ; --tw-backdrop-hue-rotate: ; --tw-backdrop-invert: ; --tw-backdrop-opacity: ; --tw-backdrop-saturate: ; --tw-backdrop-sepia: ; --tw-contain-size: ; --tw-contain-layout: ; --tw-contain-paint: ; --tw-contain-style: ; border: 0px solid rgb(229, 231, 235); margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px; user-select: initial !important;\"><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">‘বহে জলবতী ধারা প্রথম খণ্ডে রয়েছে আবদুল্লাহ আবু সায়ীদের শৈশবের স্মৃতিচারণ—করটিয়া, জামালপুর, পাবনা, বাগেরহাটের নিসর্গ আর সভ্যতায় জড়াজড়ি করা রহস্যময় পথিবী—সেই সঙ্গে ঢাকা ও কলকাতার নাগরিক জীবনের উত্তেজনা ও কল্লোল; আর এ সবকিছুর ওপর দিয়ে মুগ্ধতা। ছড়িয়ে রাখা তার ভেতরকার অনিঃশেষ বিস্ময়বোধ।</span><br style=\"--tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-pan-x: ; --tw-pan-y: ; --tw-pinch-zoom: ; --tw-scroll-snap-strictness: proximity; --tw-gradient-from-position: ; --tw-gradient-via-position: ; --tw-gradient-to-position: ; --tw-ordinal: ; --tw-slashed-zero: ; --tw-numeric-figure: ; --tw-numeric-spacing: ; --tw-numeric-fraction: ; --tw-ring-inset: ; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; --tw-blur: ; --tw-brightness: ; --tw-contrast: ; --tw-grayscale: ; --tw-hue-rotate: ; --tw-invert: ; --tw-saturate: ; --tw-sepia: ; --tw-drop-shadow: ; --tw-backdrop-blur: ; --tw-backdrop-brightness: ; --tw-backdrop-contrast: ; --tw-backdrop-grayscale: ; --tw-backdrop-hue-rotate: ; --tw-backdrop-invert: ; --tw-backdrop-opacity: ; --tw-backdrop-saturate: ; --tw-backdrop-sepia: ; --tw-contain-size: ; --tw-contain-layout: ; --tw-contain-paint: ; --tw-contain-style: ; border: 0px solid rgb(229, 231, 235); margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px; user-select: initial !important;\"><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">একটা শিশু বেড়ে উঠছে তার সময়কালস্থানের পটভূমিতে, তার চারপাশে আছে নানা চরিত্র, তার কলেজ শিক্ষক আব্বা, মূল্যবোধ সম্পন্ন শিক্ষকেরা, উনবিংশ শতাব্দীর বাঙালি রেনেসাঁসের উত্তরসূরি সংস্কৃতিবান হিন্দু সম্প্রদায়, নবজাগ্রত। মুসলমান, স্কুলের বন্ধু, মা-ভাইবোনের প্রাণবন্ত পারিবারিক জীবন, মায়ের মৃত্যুবেদনা, গ্রামের জীবনের দুঃখকষ্ট, রাজনৈতিক উত্তেজনা এমনি অনেক কিছু ।</span><br style=\"--tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-pan-x: ; --tw-pan-y: ; --tw-pinch-zoom: ; --tw-scroll-snap-strictness: proximity; --tw-gradient-from-position: ; --tw-gradient-via-position: ; --tw-gradient-to-position: ; --tw-ordinal: ; --tw-slashed-zero: ; --tw-numeric-figure: ; --tw-numeric-spacing: ; --tw-numeric-fraction: ; --tw-ring-inset: ; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; --tw-blur: ; --tw-brightness: ; --tw-contrast: ; --tw-grayscale: ; --tw-hue-rotate: ; --tw-invert: ; --tw-saturate: ; --tw-sepia: ; --tw-drop-shadow: ; --tw-backdrop-blur: ; --tw-backdrop-brightness: ; --tw-backdrop-contrast: ; --tw-backdrop-grayscale: ; --tw-backdrop-hue-rotate: ; --tw-backdrop-invert: ; --tw-backdrop-opacity: ; --tw-backdrop-saturate: ; --tw-backdrop-sepia: ; --tw-contain-size: ; --tw-contain-layout: ; --tw-contain-paint: ; --tw-contain-style: ; border: 0px solid rgb(229, 231, 235); margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px; user-select: initial !important;\"><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">একেকবার সে বড় হয়ে হতে চাইছে একেক কিছু, কখনও খেলোয়াড়, কখনও গুণ্ডা, কখনও কবি, কখনও দার্শনিক। কখনও পিতার মতোই আদর্শবাদী শিক্ষক, কখনো ট্রেনের ড্রাইভার, কখনো চার্টার্ড একাউন্ট্যান্ট—কোনটা তার নিয়তি জানা নেই। কিন্তু এসবের থেকেও এই বইয়ের যা বড় সম্পদ। তা হল জীবন-নদীর তীরে দাড়িয়ে এসব কিছুকে তাঁর। নিবিষ্টভাবে দেখে যাওয়া—শুধু চোখের দেখা নয়, বিশদভাবে। দেখা, শিল্পীর চোখ দিয়ে, কবির চোখ দিয়ে, প্রেমিকের চোখ। দিয়ে, দার্শনিকের চোখ দিয়ে দেখা—আর তাকে ব্যাখ্যা। করা, বর্ণনা করা ।</span><br style=\"--tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-pan-x: ; --tw-pan-y: ; --tw-pinch-zoom: ; --tw-scroll-snap-strictness: proximity; --tw-gradient-from-position: ; --tw-gradient-via-position: ; --tw-gradient-to-position: ; --tw-ordinal: ; --tw-slashed-zero: ; --tw-numeric-figure: ; --tw-numeric-spacing: ; --tw-numeric-fraction: ; --tw-ring-inset: ; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; --tw-blur: ; --tw-brightness: ; --tw-contrast: ; --tw-grayscale: ; --tw-hue-rotate: ; --tw-invert: ; --tw-saturate: ; --tw-sepia: ; --tw-drop-shadow: ; --tw-backdrop-blur: ; --tw-backdrop-brightness: ; --tw-backdrop-contrast: ; --tw-backdrop-grayscale: ; --tw-backdrop-hue-rotate: ; --tw-backdrop-invert: ; --tw-backdrop-opacity: ; --tw-backdrop-saturate: ; --tw-backdrop-sepia: ; --tw-contain-size: ; --tw-contain-layout: ; --tw-contain-paint: ; --tw-contain-style: ; border: 0px solid rgb(229, 231, 235); margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px; user-select: initial !important;\"><span style=\"color: rgb(51, 51, 51); font-family: __Roboto_097080, __Noto_Serif_Bengali_cb7f83; font-size: 14px; letter-spacing: normal; text-align: justify; word-spacing: 4px;\">অসাধারণ তার ভাষাশৈলী, সজীব ও প্রাণবন্ত তার বর্ণনাভঙ্গি, অথচ একই সঙ্গে রসবোধে টইটুম্বুর । একদিকে দার্শনিকের প্রজ্ঞা অন্যদিকে কবিত্বের সৌন্দর্য আর শক্তিতে প্রসাদগুণসম্পন্ন এই বইটিতে আবদুল্লাহ আবু সায়ীদ লিখে। রাখছেন তার সময় আর স্বদেশ আর স্বগ্রহের বিশ্বস্ত বয়ান, লিখে রেখে যাচ্ছেন একমহতী উপন্যাসের মতো দেশকালের প্রেক্ষাপটে ব্যক্তিমানুষের বেড়ে ওঠার ব্যক্তিগত কাহিনী—জীবন সম্পর্কে এক অভিজ্ঞ মানুষের ব্যাখ্যা। সবটা মিলিয়ে এই গ্রন্থ আমাদের সময়ের একটি শ্রেষ্ঠ বই হিসেবে। সমাদৃত হবে কালের পাঠশালায়—এই আশা মোটেও বেশি কিছু নয়।</span></p>', 'বহে জলবতী ধারা- ১ম খণ্ড', 'বহে জলবতী ধারা- ১ম খণ্ড', '[\"\\u0986\\u09a4\\u09cd\\u09ae\\u099c\\u09c0\\u09ac\\u09a8\\u09c0\"]', 221, '2014-02-01', 0, '9844584355', 'upload/book-images/7ba420c27_7605.jpg', 0, 0, 0, '2025-02-22 14:03:28', '2025-02-22 14:03:28'),
(14, 1, 2, 6, 1, 1, 1, 'হিমু', 'himu342', 240, 180, 25, 44, 1, 'হিমু আমার প্রিয় চরিত্রের একটি। যখন হিমুকে নিয়ে কিছু লিখি -নিজেকেই হিমু মনে হয়, এক ধরনের ঘোর অনুভব করি। এই ব্যাপারটা অন্য কোনো লেখার সময় তেমন করে ঘটে না। হিমুকে নিয়ে আমার প্রথম লেখা ময়ূরাক্ষি। ময়ূরাক্ষি লেখার সময় ব্যাপারটা প্রখম লক্ষ্য করি। দ্বিতীয়বার লিখলাম দরজার ওপাশে । তখনো একই ব্যাপার। কেন এরকম হয়? মানুষ হিসেবে আমি যুক্তিবাদী। হিমুর যুক্তিহীন, রহস্যময় জগৎ একজন যুক্তিবাদীকে কেন আকর্ষণ করবে? আমার জানা নেই। যদি কখনো জানতে পারি-পাঠকদের জানাবো।', '<p><span style=\"color: rgb(33, 37, 41); font-family: HindSiliguriRegular; font-size: 14px; letter-spacing: normal;\">হিমু আমার প্রিয় চরিত্রের একটি। যখন হিমুকে নিয়ে কিছু লিখি -নিজেকেই হিমু মনে হয়, এক ধরনের ঘোর অনুভব করি। এই ব্যাপারটা অন্য কোনো লেখার সময় তেমন করে ঘটে না। হিমুকে নিয়ে আমার প্রথম লেখা ময়ূরাক্ষি। ময়ূরাক্ষি লেখার সময় ব্যাপারটা প্রখম লক্ষ্য করি। দ্বিতীয়বার লিখলাম দরজার ওপাশে । তখনো একই ব্যাপার। কেন এরকম হয়? মানুষ হিসেবে আমি যুক্তিবাদী। হিমুর যুক্তিহীন, রহস্যময় জগৎ একজন যুক্তিবাদীকে কেন আকর্ষণ করবে? আমার জানা নেই। যদি কখনো জানতে পারি-পাঠকদের জানাবো।</span></p><p><span style=\"color: rgb(33, 37, 41); font-family: HindSiliguriRegular; font-size: 14px; letter-spacing: normal;\">-হুমায়ুন আহমেদ</span></p>', 'হিমু', 'হিমু', '[\"\\u09b9\\u09bf\\u09ae\\u09c1 \\u09b8\\u09bf\\u09b0\\u09bf\\u099c\",\"\\u09b9\\u09c1\\u09ae\\u09be\\u09df\\u09c1\\u09a8 \\u0986\\u09b9\\u09ae\\u09c7\\u09a6\"]', 96, '2012-02-01', 0, 'dsfsdafgsdf', 'upload/book-images/0e743778fca4_1277.gif', 0, 0, 0, '2025-02-22 14:11:03', '2025-02-22 14:11:03'),
(15, 5, 15, 13, 11, 1, 0, 'আদমবোমা', 'fgfgf', 650, 488, 25, 15, 1, '‘যে কোন মূল্যে’ স্বাধীনতা ও সার্বভৌমত্ব রক্ষা করিতে হইবে। এই শপথবাক্য নিত্য যাঁহারা উচ্চারণ করিয়া থাকেন তাহারা নিজেদের বলিয়া থাকেন ‘স্বাধীনতার অতন্দ্র প্রহরী’। সবার উপরে স্বাধীনতা সত্য', '<div class=\"summary-description active\" id=\"js--summary-description\">‘যে কোন মূল্যে’ স্বাধীনতা ও সার্বভৌমত্ব রক্ষা করিতে হইবে। এই শপথবাক্য নিত্য যাঁহারা উচ্চারণ করিয়া থাকেন তাহারা নিজেদের বলিয়া থাকেন ‘স্বাধীনতার অতন্দ্র প্রহরী’। সবার উপরে স্বাধীনতা সত্য, তাহার উপরে নাই। স্বাধীনতার নিকট শুদ্ধ প্রাণ নহে, খোদ ন্যায়বিচারও তুচ্ছ। এমনকি পরমাণু বোমার বিস্ফোরণ ঘটাইতে হইলেও স্বাধীনতা রক্ষা করা ন্যায্য। যাঁহারা এই দাবি তোলেন তাঁহাদের্ অপর নাম প্রকৃত প্রস্তাবে ‘স্বাধীনতা-ব্যবসায়ী’ বা লিবারেল। <br> <br>এই বইয়ের ১৮টি প্রবন্ধে সলিমুল্লাহ খান নানান দিক হইতে প্রশ্ন তুলিয়াছেন লিবারেলিজম বা স্বাধীনতা-ব্যবসায় মতবাদটি কেন এই স্ববিরোধে পরিপূর্ণ। তাঁহার বিচারে আদমবোমা শুদ্ধ পদার্থ নহে, পদও বটে। পদ ও পদার্থ যেখানে একাকার হয় তাহাকে বলে সম্বল বা সিম্বল। আত্মহত্যা করিয়া কেহ স্বাধীন হয় না এ কথা সত্য কিন্তু আত্মহত্যার সম্ভাবনা হিসাবে না লইলে স্বাধীনতার কোনো ভিত্তিই থাকে না। মহাত্মা গান্ধি হইতে স্যার বিদিয়াধর সুরজপ্রসাদ নাইপল, পণ্ডিত এডোয়ার্ড সায়িদ হইতে কবি শামসুর রাহমান পর্যন্ত স্বাধীনতা-ব্যবসায়ের নানান রূপ এই বইতে দেখানো হইয়াছে। <br> <br>তাহা হইলে মানব জাতির ভবিষ্যত কী? </div>', 'আদমবোমা', 'আদমবোমা', '[\"\\u09ac\\u09bf\\u09ac\\u09bf\\u09a7 \\u09ac\\u09bf\\u09b7\\u09df\\u0995 \\u09aa\\u09cd\\u09b0\\u09ac\\u09a8\\u09cd\\u09a7\"]', 293, '2013-02-05', 0, '9789840415847', 'upload/book-images/47fd7dd90_2367.jpg', 0, 0, 0, '2025-02-23 23:12:33', '2025-02-23 23:12:33');
