INSERT INTO `book_series` (`id`, `author_id`, `name`, `description`, `status`, `created_at`, `updated_at`) VALUES
(1, 1, 'হিমু সিরিজ', '<p style=\"margin: 0.5em 0px 1em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; white-space: normal; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">হিমু&nbsp;বাংলাদেশের কথাসাহিত্যিক&nbsp;<a href=\"https://bn.wikipedia.org/wiki/%E0%A6%B9%E0%A7%81%E0%A6%AE%E0%A6%BE%E0%A6%AF%E0%A6%BC%E0%A7%81%E0%A6%A8_%E0%A6%86%E0%A6%B9%E0%A6%AE%E0%A7%87%E0%A6%A6\" class=\"mw-redirect\" title=\"হুমায়ুন আহমেদ\" style=\"text-decoration: none; color: var(--color-progressive,#36c); background: none; border-radius: 2px; overflow-wrap: break-word;\">হুমায়ুন আহমেদ</a>&nbsp;সৃষ্ট একটি জনপ্রিয় কাল্পনিক চরিত্র। হিমু মূলত একজন বেকার যুবক&nbsp;; যার আচরণে বেখেয়ালী, জীবনযাপনে ছন্নছাড়া ও বৈষয়িক ব্যাপারে সম্পূর্ণ উদাসীন ভাব প্রকাশ পায়। চাকরির সুযোগ থাকলেও সে চাকরি কখনো করে না বলেই সে বেকার। তার অস্বাভাবিক চরিত্রের মধ্যে সে হলুদ পাঞ্জাবি পরে খালি পায়ে রাস্তাঘাটে দিন-রাত ঘুরে বেড়ায় এবং মাঝে মাঝে ভবিষ্যদ্বাণী করে মানুষকে চমকে দেয়।</p><p style=\"margin: 0.5em 0px 1em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; white-space: normal; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">নব্বই দশকে হিমুর প্রথম উপন্যাস&nbsp;<a href=\"https://bn.wikipedia.org/wiki/%E0%A6%AE%E0%A6%AF%E0%A6%BC%E0%A7%82%E0%A6%B0%E0%A6%BE%E0%A6%95%E0%A7%8D%E0%A6%B7%E0%A7%80\" class=\"mw-disambig\" title=\"ময়ূরাক্ষী\" style=\"text-decoration: none; color: var(--color-progressive,#36c); background: none; border-radius: 2px; overflow-wrap: break-word;\">ময়ূরাক্ষী</a>&nbsp;প্রকাশিত হয়। প্রাথমিক সাফল্যের পর&nbsp;হিমু&nbsp;চরিত্র বিচ্ছিন্নভাবে হুমায়ুন আহমেদের বিভিন্ন উপন্যাসে প্রকাশিত হতে থাকে।&nbsp;হিমু&nbsp;ও&nbsp;<a href=\"https://bn.wikipedia.org/wiki/%E0%A6%AE%E0%A6%BF%E0%A6%B8%E0%A6%BF%E0%A6%B0_%E0%A6%86%E0%A6%B2%E0%A6%BF\" title=\"মিসির আলি\" style=\"text-decoration: none; color: var(--color-progressive,#36c); background: none; border-radius: 2px; overflow-wrap: break-word;\">মিসির আলি</a>&nbsp;হুমায়ুন আহমেদ সৃষ্ট সর্বাধিক জনপ্রিয় দু’টি কাল্পনিক চরিত্র। উদাসীন হিমু একবিংশ শতাব্দীর প্রথম দশকের বাঙালি তরুণদের ব্যাপকভাবে অনুপ্রাণিত করেছিল।</p><p></p>', 1, '2025-01-18 23:55:26', '2025-01-18 23:55:26'),
(2, 1, 'মিসির আলি সিরিজ', '<p><b style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; white-space: normal; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">মিসির আলি</b><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; white-space: normal; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">, বাংলাদেশের প্রখ্যাত ঔপন্যাসিক<span>&nbsp;</span></span><a href=\"https://bn.wikipedia.org/wiki/%E0%A6%B9%E0%A7%81%E0%A6%AE%E0%A6%BE%E0%A6%AF%E0%A6%BC%E0%A7%82%E0%A6%A8_%E0%A6%86%E0%A6%B9%E0%A6%AE%E0%A7%87%E0%A6%A6\" title=\"হুমায়ূন আহমেদ\" style=\"text-decoration: none; color: var(--color-progressive,#36c); background: none rgb(255, 255, 255); border-radius: 2px; overflow-wrap: break-word; font-family: sans-serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; white-space: normal;\">হুমায়ূন আহমেদ</a><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; white-space: normal; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\"><span>&nbsp;</span>সৃষ্ট একটি জনপ্রিয় চরিত্র। মিসির আলি কাহিনীগুলো রহস্যমাত্রিক। মিসির আলির কাহিনীগ‌ুলো ঠিক গোয়েন্দা কাহিনী নয়, কিংবা \'ক্রাইম ফিকশন\' বা \'থ্রিলার\'-এর মতো খুনি-পুলিশের ধাওয়া-পাল্টা-ধাওয়া নয়, বরং মনস্তাত্ত্বিক, বিজ্ঞাননির্ভর এবং যুক্তিনির্ভর কাহিনীর বুনটে বাঁধা। অনেক ক্ষেত্রে একে রহস্যগল্প বলা চলে। চারিত্রিক দিক দিয়ে মিসির আলি চরিত্রটি হুমায়ূন আহমেদের আরেক অনবদ্য সৃষ্টি<span>&nbsp;</span></span><a href=\"https://bn.wikipedia.org/wiki/%E0%A6%B9%E0%A6%BF%E0%A6%AE%E0%A7%81\" class=\"mw-redirect\" title=\"হিমু\" style=\"text-decoration: none; color: var(--color-progressive,#36c); background: none rgb(255, 255, 255); border-radius: 2px; overflow-wrap: break-word; font-family: sans-serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; white-space: normal;\">হিমু</a><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; white-space: normal; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\"><span>&nbsp;</span>চরিত্রটির পুরোপুরি বিপরীত। তরুণ হিমু চলে প্রতি-যুক্তির (anti-logic) তাড়নায়, অপরপক্ষে বয়োজ্যেষ্ঠ মিসির আলি অনুসরণ করেন বিশ‌ুদ্ধ যুক্তি (pure logic)। এই যুক্তিই মিসির আলিকে রহস্যময় জগতের প্রকৃত স্বরূপ উদঘাটনে সাহায্য করে। সেসব কাহিনীর প্রতিফলন ঘটেছে মিসির আলি সম্পর্কিত প্রতিটি উপন্যাসে।</span></p>', 1, '2025-01-18 23:55:58', '2025-01-18 23:55:58');

